package Arrays;

public class singleDimensionalArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int array1[]=new int[4];
		
		array1[0]=10;
		array1[1]=20;
		array1[2]=30;
		array1[3]=40;
		
				
		for(int i=0;i<=3;i++)
		{
			System.out.println(i+"- "+array1[i]);
		}
		

	}

}
