package Loops;

public class whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		String string="LiveTech";
		while(a<=5)
		{
			System.out.println(a+" "+string);
			a++;
		}
		String string1="Selenium";
		int y=-10;
		while(y<=0)
		{
			System.out.println(y+" "+string1);
			y=y+5;
		}

	}

}
